        <section id='projects' className='projects'>
            <div className="flux">
            <h2 className='text_title projects_title'># Projets</h2>
            <p className='projects_item-text'>Voici une sélection des projets les plus représentatifs de mon savoir-faire.</p>
            <div className='projects_grid flux'> 
            { projectList.map((project) => (
                <Card key={project.id} id={project.id} name={project.name} title={project.title} subTitle={project.subTitle} image={project.imageURL} tags={project.tags} category={project.categorie}/>
            ))}            
            </div>
            </div>
        </section>

